mapper.insert:

insert into miniprogram_suggestion (id, user_id, image,
  title, detail, type,
  num, create_time, update_time
  )
values (#{id,jdbcType=BIGINT}, #{userId,jdbcType=VARCHAR}, #{image,jdbcType=VARCHAR},
  #{title,jdbcType=VARCHAR}, #{detail,jdbcType=VARCHAR}, #{type,jdbcType=INTEGER},
  #{num,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select
,(select count(1) from miniprogram_approval where id = suggestion_id and miniprogram_approval.user_id=#{request.openid,jdbcType=VARCHAR} ) as approval
from miniprogram_suggestion

  where type = #{request.type,jdbcType=INTEGER}


  where id = #{request.id,jdbcType=INTEGER}

order by num desc
